const animals = $input.first().json.animals;
const img_logo = $('Constants').item.json.img_logo;
const fav_icon = $('Constants').item.json.fav_icon;
const dog_font= $('Constants').item.json.dog_font;
const webhook_url = $('Vars').first().json.webhookUrl;
const canil_id = $('Vars').first().json.canil_id;

const APPLIED_STATUS = ( $('Vars').item.json.query.status || 'Abrigado');

const getIcon = (key) => icones?.[0]?.[key]?.value ?? null;

let cardsHtml = '';

// Função para remover acentos de uma string
function normalizeAccents(str) {
    if (!str) return '';
    return str.normalize('NFD').replace(/[\u0300-\u036f]/g, "");
}

function animalPhoto(especie = "Vazio") {
  const pic_url = {
    "Cachorro": "https://i.ibb.co/Z6dPncCH/pic-dog.png",
    "Gato": "https://i.ibb.co/9dWLkZs/pic-cat.png",
    "Vazio": "https://i.ibb.co/KpVTx4vK/pic-none.png"
  };

  // Retorna a imagem correspondente ou a de "Vazio" como fallback
  return pic_url[especie] || pic_url["Vazio"];
}


if (animals && Array.isArray(animals) && animals.length > 0) {
    cardsHtml = animals.map(animal => {
        // Normaliza o nome do animal para remover acentos
        const normalizedName = normalizeAccents(animal.nome);

        // Exibe os dados, exceto album, canil e animal_id
        return `
            <a href="${webhook_url}?animal_id=${animal.animal_id}" class="animal-list-link">
                <div class="animal-strip">
                    <div class="animal-photo-list-container">
                        <img class="animal-photo-list" src="${animal.foto || animalPhoto(animal.especie)}" alt="Foto de ${normalizedName}"/>
                    </div>
                    <div class="list-content">
                        <h3>${normalizedName}</h3>
                        <div class="characteristics-container">
                            <span class="characteristic-box">${animal.especie}</span>
                            <span class="characteristic-box">${animal.sexo}</span>
                            <span class="characteristic-box">${animal.faixa_etaria}</span>
                            <span class="characteristic-box">${animal.porte}</span>
                        </div>
                    </div>
                </div>
            </a>
        `;
    }).join('');
} else {
    cardsHtml = '<p class="text-red-500 text-center">Nenhum animal encontrado para adoção no momento.</p>';
}

const html = `
