            .status-filter-btn {
                cursor: pointer;
                background-color: #f0f2f5;
                color: var(--text-dark);
                padding: 8px 16px; /* Aumentei o padding */
                border-radius: 8px;
                font-size: 0.9rem; /* Aumentei a fonte */
                font-weight: 500;
                border: none;
                transition: background-color 0.2s ease, color 0.2s ease;
            }
            .status-filter-btn:hover {
                background-color: #e2e4e7;
