        <div class="header">
            <div class="header-content" style="justify-content: space-between; width: 100%;">
                <div style="display: flex; align-items: center; gap: 10px;">
                    <img class="header-logo" src="${img_logo}" alt="Logo Kaniu"/>
                </div>
            </div>
        </div>
        <div class="filter-container">
          <button class="status-filter-btn ${APPLIED_STATUS === 'Abrigado' ? 'active' : ''}" data-status="Abrigado">Abrigados</button>
          <button class="status-filter-btn ${APPLIED_STATUS === 'Adotado' ? 'active' : ''}" data-status="Adotado">Adotados</button>
          <button class="status-filter-btn ${APPLIED_STATUS === 'Internado' ? 'active' : ''}" data-status="Internado">Internados</button>
          <button class="status-filter-btn ${APPLIED_STATUS === 'Desaparecido' ? 'active' : ''}" data-status="Desaparecido">Desaparecidos</button>
          <button class="status-filter-btn ${APPLIED_STATUS === 'Falecido' ? 'active' : ''}" data-status="Falecido">Falecidos</button>
        </div>
        <div class="main-container">
            <div class="list-container">
                ${cardsHtml}
            </div>
        </div>

        <script>
          (function () {
            const ENDPOINT_URL = ${JSON.stringify(webhook_url)};
            const DEFAULT_STATUS = 'Abrigado';
            // Valor decidido no servidor (sem depender da URL)
            const CURRENT_STATUS = ${JSON.stringify(APPLIED_STATUS)};

            // Atualiza classe ativa (segurança extra caso futuramente re-renderize via JS)
            const statusButtons = document.querySelectorAll('.status-filter-btn');
            function updateButtonStatus() {
              statusButtons.forEach(btn => {
                const isActive = btn.getAttribute('data-status') === CURRENT_STATUS;
                btn.classList.toggle('active', isActive);
              });
            }
            updateButtonStatus();

            // Clique nos botões → navega com ?status=... (sem redirecionar no primeiro load)
            statusButtons.forEach(button => {
              button.addEventListener('click', () => {
                const statusToFilter = button.getAttribute('data-status');
                const nextStatus = (statusToFilter === CURRENT_STATUS) ? DEFAULT_STATUS : statusToFilter;
                const newUrl = ENDPOINT_URL+'?status='+encodeURIComponent(nextStatus);
                window.location.href = newUrl;
              });
            });
          })();
        </script>

    </body>
